import React from 'react'
import './tasks.scss'
import {useState,useEffect} from 'react'
import {v4 as myId} from 'uuid'
export const Tasks = () => {
  const[itemToDo,setItemToDo]=useState("")
  const handleFunction=(event)=>{
    setItemToDo(event.target.value)
  }
  return (
    <>
    <div className="card" style={{width: '14rem'}}>
        {/* <img src="..." className="card-img-top" alt="..."/> */}
    <div className="card-body">
        <h5 className="card-title">Tasks</h5>
        <p className="card-text">
        <input class="checkbox-pull" type="checkbox" id="check3"/>
    <label for="check3"><span></span>Pull Me</label>
            <label class="container">
            Как ты провел день? 
            <input value={itemToDo}
            type="text"
            className="form-control"
            onChange={handleFunction}
        />
            </label>
        </p>
        <button href="#" className="btn btn-primary">Submit</button>
    </div>
    </div>
    </>
  )
}
