import React, { createRef, useState } from 'react'
import cat from './chibi-cat.png'
import './chibi.css'
import { animated, useSpring } from "react-spring";
import styled, { ThemeProvider } from "styled-components";



const AnimatedItem = styled(animated.div)`
  width: 100,
  background: "brand"
`;

export const Chibi = () => {
  const [animate,setAnimate]=useState(false)
  const springProps = useSpring({
    loop: { reverse: true },
    from: { y: 0 },
    to: { y: 100},
    config: { duration: 500 }
  });
  function setjump(){
    if(animate){
      return springProps
    }
    return  ''
  }
  return (
    <>
            <animated.img
            src={cat}
            alt={'cat'}
            className='cat'
            onClick={()=>setjump()}
            style={setju} />

    </>
  )
}
