import React from 'react'
import './tasks.scss'
import {useState,useEffect} from 'react'
import {v4 as myId} from 'uuid'
export const Tasks = () => {
  const[itemToDo,setItemToDo]=useState("")
  const[items,setItems]=useState({})
  const handleFunction=(event)=>{
    setItemToDo(event.target.value)
  }
  const addItem=()=>{
    const newItem={key:myId(),label:itemToDo}
    setItems(newItem)
    setItemToDo("")
  }
  const handleDelete=({key})=>{
    setItems(items.filter((item) => {
        return  item.key !== key
      }
      )
    );
  };
  return (
    <>
    <div className="card" style={{width: '14rem'}}>
        {/* <img src="..." className="card-img-top" alt="..."/> */}
    <div className="card-body">
        <h5 className="card-title">Tasks</h5>
        <p className="card-text">
        <input className="checkbox-pull" type="checkbox" id="check3"/>
    <label htmlFor="check3"><span></span>Pull Me</label>
            <label class="container">
            Как ты провел день? 
            <input value={itemToDo}
            type="text"
            className="form-control"
            onChange={handleFunction}
        />
            </label>
        </p>
        <button href="#" className="btn btn-primary" 
        onClick={()=>addItem()}>Submit</button>
    </div>
    </div>
    <div class='speech-bubble'>
      {items.label? items.label:'Here your text'}
      <button
              onClick={()=>handleDelete(items.key)}
              type="button"
            >
              <i className="fa fa-trash-o" />
            </button>
    </div>
    </>
  )
}
