import React, { useState,useRef, useEffect } from 'react'
import cat from './chibi-cat.png'
import './chibi.css'
import { animated, useSpring } from "react-spring";



export const Chibi = ({stairsRef,tasksDone}) => {
  const days = 1;
  const catRef = useRef(null);
  const [animate,setAnimate]=useState(false)
  const [catRect,setCatRect]=useState({
      x:0,
      y:0,
      width:0,
      height:0   
  })
  useEffect(()=>{
    const div = stairsRef.current;
    const rect = div.getBoundingClientRect();
    setCatRect({
      x:rect.x,
      y:rect.y,
      width:rect.width,
      height:rect.height})
  },[])
  const {x,y,width,height}=catRect
  let springProps = null
  let springProps2 = null
  if(days===1){
  springProps=useSpring({
    // loop: { reverse: animate},
    from: { bottom: 0+'px', left: '0%', width: '10%', height: '10%',position: 'relative' },
    to: async next=> { 
        await next({ bottom: height+90+'px', left: '10%'})
        await next({ bottom: (height/2+60)+'px'})        
  },
    config: { duration: 500 }
  });
  springProps2 = useSpring({
    // loop: { reverse: animate},
    from: { bottom: 0+'px', left: '0%', width: '10%', height: '10%',position: 'relative' },
    to: async next=> { 
        await next({ bottom: 0+'px', left: '0%', width: '10%', height: '10%',position: 'relative' })  
  },
    config: { duration: 500 }
  });
}
 springProps = useSpring({
    // loop: { reverse: animate},
    from: { bottom:(height/2+60)+'px', left: '10%', width: '10%', height: '10%',position: 'relative' },
    to: async next=> { 
        await next({ bottom: height+120+'px', left: '30%'})
        await next({ bottom: (height/2+90)+'px'})        
  },
    config: { duration: 500 }
  });
  springProps2 = useSpring({
    // loop: { reverse: animate},

    from: { bottom:(height/2+60)+'px', left: '10%', width: '10%', height: '10%',position: 'relative' },
    to: async next=> { 
        await next({ bottom:(height/2+60)+'px', left: '10%', width: '10%', height: '10%',position: 'relative' })  
  },
    config: { duration: 500 }
  });

  return (
    <>
            <animated.img
            ref={catRef}
            src={cat}
            alt={'cat'}
            className='cat'
            style={
              tasksDone?
              springProps:
              springProps2} />

    </>
  )
}
