import React, { useState,useRef, useEffect } from 'react'
import cat from './chibi-cat.png'
import './chibi.css'
import { animated, useSpring } from "react-spring";
import styled, { ThemeProvider } from "styled-components";



const AnimatedItem = styled(animated.div)`
  width: 100,
  background: "brand"
`;

export const Chibi = () => {
  const catRef = useRef(null);
  const [animate,setAnimate]=useState(false)
  var plus = 0;

  const springProps = useSpring({
    // loop: { reverse: animate},
    from: { top: '50%', left: '0%', width: '10%', height: '10%', backgroundColor: 'lightgreen', position: 'relative' },
    to: async next=> {
      while(1){
        await next({ top: '10%', left: '10%', backgroundColor: 'lightblue' })
        await next({ top: '0%', backgroundColor: 'lightgreen' })        
        await next({ top: '0%', backgroundColor: 'lightgoldenrodyellow' })
        await next({  backgroundColor: 'lightpink' })
        await next({  left: '0%', backgroundColor: 'lightsalmon' })
        await next({  backgroundColor: 'lightcoral' })
        await next({ top: '0%', backgroundColor: 'lightseagreen' })
        await next({  backgroundColor: 'lightskyblue' })
        await next({ left: '0%', top: '0%', backgroundColor: 'lightslategrey' })
      }

  },
    config: { duration: 500 }
  });

  console.log(plus)
  // useEffect(()=>{
  //   const div = catRef.current;
  //   const rect = div.getBoundingClientRect();
  //   console.log(rect.x,rect.y)
  // })
  return (
    <>
            <animated.img
            ref={catRef}
            src={cat}
            alt={'cat'}
            className='cat'
            onClick={(obj)=>{
              setTimeout(setAnimate(!animate),3000)

            }}
            style={springProps} />

    </>
  )
}
