import React from 'react'
import { useEffect } from 'react';
import './Stair.scss'
import $ from 'jquery';
export const Stair = () => {
    function bounce(bouncingCount, speed) {
		var top = 200;
		var speedRatio = speed / top;
		var heightRatio = top / bouncingCount;

		for (var i = 1; i <= bouncingCount; i++) {

			$('#bouncing-ball').animate({
				'bottom' : top,
                'left':'+=60px'
			}, speed);
			$('#bouncing-ball').animate({
				'bottom' : 100
			}, speed / 2);
            top+=50;
			// top = top - (heightRatio);
			// speed = speedRatio * top;
		}
	}

	$("#bouncing-ball").click(function() {
		bounce(1, 500);
	});
  return (
    <>
    <div className="stairs">
        <div className="base"> </div>
            <div className="step" style = {{"--j":1}}>
                <i></i>
                <i></i>
            </div>
            <div className="step" style ={ {"--j":2}}>
                <i></i>
                <i></i>
            </div>
            <div className="step" style = {{"--j":3}}>
                <i></i>
                <i></i>
            </div>
            <div className="step" style = {{"--j":4}}>
                <i></i>
                <i></i>
            </div>
            <div className="step" style = {{"--j":5}}>
                <i></i>
                <i></i>
            </div>
            <div className="step" style = {{"--j":6}}>
                <i></i>
                <i></i>
        </div>
     </div>
    </>
  )
}
