import React, { useState,useRef, useEffect } from 'react'
import cat from './chibi-cat.png'
import './chibi.css'
import { animated, useSpring } from "react-spring";



export const Chibi = () => {
  const catRef = useRef(null);
  const [animate,setAnimate]=useState(false)
  var plus = 0;

  const springProps = useSpring({
    // loop: { reverse: animate},
    from: { bottom: '45px', left: '0%', width: '10%', height: '10%',position: 'relative' },
    to: async next=> {
      while(1){
        await next({ bottom: '150px', left: '10%'})
        await next({ bottom: '90px'})        
        await next({ bottom: '90px'})
        await next({ left: '0%', bottom: '90px'})
      }

  },
    config: { duration: 500 }
  });

  console.log(plus)
  // useEffect(()=>{
  //   const div = catRef.current;
  //   const rect = div.getBoundingClientRect();
  //   console.log(rect.x,rect.y)
  // })
  return (
    <>
            <animated.img
            ref={catRef}
            src={cat}
            alt={'cat'}
            className='cat'
            onClick={(obj)=>{
              setTimeout(setAnimate(!animate),3000)

            }}
            style={springProps} />

    </>
  )
}
