import React from 'react'
import cat from './chibi-cat.png'
import './chibi.css'
import { animated, useSpring } from "react-spring";
import styled, { ThemeProvider } from "styled-components";
import { Box, Container, Heading, Typography, Flex, theme } from "../../ui";

//import "../../ui/molecules/global-styles/global.css";

const AnimatedItem = styled(animated(Box))``;
AnimatedItem.defaultProps = {
  size: 100,
  bg: "brand"
};

const AnimatedBox = styled(animated(Box))``;
AnimatedBox.defaultProps = {};


export const Chibi = () => {
  const springProps = useSpring({
    loop: { reverse: true },
    from: { y: 0, rotate: 0 },
    to: { y: 100, rotate: 180 },
    config: { duration: 2500 }
  });
  return (
    <>
    <ThemeProvider theme={theme}>
      <Box bg="bg100" minHeight="100vh" py={1}>
        <Container>
          <Heading textAlign="center">
            React Spring (v9) Example - useSpring + loop
          </Heading>
          <Typography textAlign="center">
            Click on the box to trigger animation
          </Typography>
          <Flex
            justifyContent="center"
            alignItems="center"
            flexDirection="column"
            pt={6}
          >
            <AnimatedItem style={springProps} />
          </Flex>
        </Container>
      </Box>
    </ThemeProvider>
    <img className='cat' src={cat} />
    </>
  )
}
