import React, { useState,useRef, useEffect } from 'react'
import cat from './chibi-cat.png'
import './chibi.css'
import { animated, useSpring } from "react-spring";
import styled, { ThemeProvider } from "styled-components";



const AnimatedItem = styled(animated.div)`
  width: 100,
  background: "brand"
`;

export const Chibi = () => {
  const catRef = useRef(null);
  const [animate,setAnimate]=useState(false)
  var plus = 0;

  // const springProps = useSpring({
  //   loop: { reverse: animate},
  //   from: { 
  //   transform: 'translateY(0px)',
  //   transform: 'translateX(0px)'},
  //   to: {
  //   transform: 'translateY(100px)',
  //   transform: 'translaateX(50px)',
  //   transform: 'translateZ(100px)'
  // },
  //   config: { duration: 500 }
  // });
  const springProps  = useSpring({
    from: { top: '50%', left: '0%', width: '0%', height: '0%', position: 'relative' },
    to: async next => {
      while (1) {
        await next({ width: '80%', height: '80%', top: '10%', left: '10%'})
        await next({ top: '0%', left: '50%', height: '40%', width: '50%' })        
        await next({ top: '0%', height: '100%' })
        await next({ top: '60%', height: '40%'})
        await next({ width: '100%', left: '0%' })
        await next({ width: '50%' })
        await next({ top: '0%', height: '100%'})
        await next({ height: '40%'})
        await next({ left: '0%', top: '0%', width: '100%', height: '100%', backgroundColor: 'lightslategrey' })
      }
    },
  })
  console.log(plus)
  useEffect(()=>{
    const div = catRef.current;
    const rect = div.getBoundingClientRect();
    console.log(rect.x,rect.y)
  })
  return (
    <>
            <animated.img
            ref={catRef}
            src={cat}
            alt={'cat'}
            className='cat'
            onClick={(obj)=>{
              setTimeout(setAnimate(!animate),3000)

            }}
            style={springProps} />

    </>
  )
}
