import React from 'react'
import cat from './chibi-cat.png'
import './chibi.css'
import { animated, useSpring } from "react-spring";
import styled, { ThemeProvider } from "styled-components";
import { Box, Container, Heading, Typography, Flex, theme } from "../../ui";

// import styled from 'styled-components';

import {
  background,
  border,
  bottom,
  boxShadow,
  color,
  compose,
  layout,
  left,
  position,
  right,
  space,
  top,
  typography,
} from 'styled-system';

export const composedHelpers = compose(
  background,
  layout,
  border,
  space,
  color,
  typography,
  top,
  bottom,
  left,
  right,
  position,
  boxShadow,
);

// export const Box = styled.div`
//   ${composedHelpers}
// `;

Box.defaultProps = {};
const AnimatedItem = styled(animated(Box))``;
AnimatedItem.defaultProps = {
  size: 100,
  bg: "brand"
};

const AnimatedBox = styled(animated(Box))``;
AnimatedBox.defaultProps = {};


export const Chibi = () => {
  const springProps = useSpring({
    loop: { reverse: true },
    from: { y: 0, rotate: 0 },
    to: { y: 100, rotate: 180 },
    config: { duration: 2500 }
  });
  return (
    <>
    <ThemeProvider theme={theme}>
      <Box bg="bg100" minHeight="100vh" py={1}>       
            <AnimatedItem style={springProps} />
      </Box>
    </ThemeProvider>
    <img className='cat' src={cat} />
    </>
  )
}
