import React, { createRef, useState } from 'react'
import cat from './chibi-cat.png'
import './chibi.css'
import { animated, useSpring } from "react-spring";
import styled, { ThemeProvider } from "styled-components";



const AnimatedItem = styled(animated.div)`
  width: 100,
  background: "brand"
`;

export const Chibi = () => {
  const catRef = use
  const [animate,setAnimate]=useState(false)
  const springProps = useSpring({
    loop: { reverse: animate },
    from: { y: 0},
    to: { y: 100},
    config: { duration: 500 }
  });

  return (
    <>
            <animated.img
            ref={obj=>{
              console.log(obj.getBoundingClientRect())       
            }}
            src={cat}
            alt={'cat'}
            className='cat'
            onClick={(obj)=>{
              setAnimate(!animate)

            }}
            style={springProps} />

    </>
  )
}
