import { Camera } from 'three';

import { NodeFrame } from 'three-stdlib/nodes/core/NodeFrame';
import { TempNode } from 'three-stdlib/nodes/core/TempNode';
import { FunctionNode } from 'three-stdlib/nodes/core/FunctionNode';
import { FloatNode } from 'three-stdlib/nodes/inputs/FloatNode';

export class CameraNode extends TempNode {
    constructor(scope?: string, camera?: Camera);

    scope: string;
    near: FloatNode | undefined;
    far: FloatNode | undefined;
    camera: Camera | undefined;
    updateFrame: boolean | undefined;
    nodeType: string;

    setCamera(camera: Camera): void;
    setScope(scope: string): void;
    onUpdateFrame(frame: NodeFrame): void;
    copy(source: CameraNode): this;

    static Nodes: {
        depthColor: FunctionNode;
    };
    static POSITION: string;
    static DEPTH: string;
    static TO_VERTEX: string;
}
