import { Node } from 'three-stdlib/nodes/core/Node';
import { FunctionNode } from 'three-stdlib/nodes/core/FunctionNode';
import { TempNode } from 'three-stdlib/nodes/core/TempNode';

export class FunctionCallNode extends TempNode {
    constructor(func: FunctionNode, inputs?: Node[]);

    nodeType: string;

    value: FunctionNode;
    inputs: Node[];

    setFunction(func: FunctionNode, inputs?: Node[]): void;
    getFunction(): FunctionNode;
    getType(): string;
    copy(source: FunctionCallNode): this;
}
