import React, { useEffect, useState } from 'react'
import './Stair.scss'
import { useContext } from 'react'
import live from '../../images/heart.png'
import gift from '../../images/gift.png'
import { TaskContext } from '../Context'
import { Navigation } from '../navbar/Navbar'
import { ethers } from "ethers"
import { Spinner } from 'react-bootstrap'

export const Stair = (props) => {
  const [loading, setLoading] = useState(true)
  const [account, setAccount] = useState(null)
  const [nft, setNFT] = useState({})
  const [marketplace, setMarketplace] = useState({})
  // MetaMask Login/Connect
  const web3Handler = async () => {
    const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
    setAccount(accounts[0])
    // Get provider from Metamask
    const provider = new ethers.providers.Web3Provider(window.ethereum)
    // Set signer
    const signer = provider.getSigner()

    window.ethereum.on('chainChanged', (chainId) => {
      window.location.reload();
    })

    window.ethereum.on('accountsChanged', async function (accounts) {
      setAccount(accounts[0])
      await web3Handler()
    })
    loadContracts(signer)
  }
  const loadContracts = async (signer) => {
    // Get deployed copies of contracts
    const marketplace = new ethers.Contract(MarketplaceAddress.address, MarketplaceAbi.abi, signer)
    setMarketplace(marketplace)
    const nft = new ethers.Contract(NFTAddress.address, NFTAbi.abi, signer)
    setNFT(nft)
    setLoading(false)
  }
    const {stairsRef,isClicked,taskDone,daysArray,savedTasksDone} = props
    const stairsCount = 23
    const { setIsClicked} = useContext(TaskContext)
    const { setId} = useContext(TaskContext)
    const clickHeart = (i)=>{
        setId(i)
        setIsClicked(!isClicked)
    }
    const liveClass =()=>{
    let live;
    [...Array(stairsCount)].map((e, i) => {
    for(let i2 = 0;i2<daysArray.length;i2++){
        if(i===daysArray[i2]){
          live = "live-taskDone"
        }
        }
    }
    )
    return live;
}

  return (
    <>
    <img className='gift' src={gift} alt='image'/>
     <div className="stairs" >
            <div className={`${taskDone? 'step-taskDone': 'step'}`}
            ref={stairsRef}
            style = {{"--j":1}}>
                <i ><img onClick={
                    ()=>{setIsClicked(!isClicked)}
                    } 
                    style={{width:'75px',margin:'0px'}}
                     src={live}/></i>
                <i></i>
            </div>
            {
            [...Array(stairsCount)].map((e, i) => 
            (i!==0)? 
            <div className='step'
                key={i}
                style = {{"--j":i}}>

                    <i >
                      <div className='con'>
                      <img onClick={
                        ()=>{
                            clickHeart(i)
                        }
                        } 
                        className={`${liveClass()}`}
                        key={i}
                        src={live}/>
                        <p>{i===22?'Win':i}</p>
                        </div>
                        </i>
                    <i></i>
                </div>:''
            )}
     </div>
     <Navigation web3Handler={web3Handler} account={account} />
    </>
  )
}
