import React, { useEffect, useState } from 'react'
import './Stair.scss'
import { useContext } from 'react'
import live from '../../images/heart.png'
import gift from '../../images/gift.png'
import prize from '../../images/prize.png'
import { TaskContext } from '../Context'
import { animated, useSpring } from "react-spring";

export const Stair = ({stairsRef,isClicked,id}) => {
    const stairsCount = 23
    const { setIsClicked} = useContext(TaskContext)
    const { setId} = useContext(TaskContext)
    const clickHeart = (i)=>{
        setId(i)
        setIsClicked(!isClicked)
    }

    const springProps = useSpring({
        from: { bottom: height * days + 'px', left: width * days + 'px', width: '100px', height: '100px', position: 'relative' },
        to: async next => {
          await next({ bottom: height * days +70 + 'px', left: width * days + 'px' })
          await next({ bottom: height * days + 30 + 'px', left: width * days + width / 2 + 'px' })
        },
        config: { duration: 500 }
      });

  return (
    <>
    <img className='gift' src={gift} alt='image'/>
    <img className='prize' src={prize}/>
     <div className="stairs" >

        <div className="base"> </div>

            <div className="step"
            ref={stairsRef}
            style = {{"--j":1}}>
                <i ><img onClick={
                    ()=>{setIsClicked(!isClicked)}
                    } 
                    style={{width:'75px',margin:'0px'}}
                     src={live}/></i>
                <i></i>
            </div>
            {
            [...Array(stairsCount)].map((e, i) => 
            (i!==0)? 
            <div className="step"
                key={i}
                style = {{"--j":i}}>
                    <i><img onClick={
                        ()=>{
                            clickHeart(i)
                        }
                        } 
                        style={{width:'75px',margin:'0px'}} 
                        src={live}/></i>
                    <i></i>
                </div>:''
            )}
     </div>
    </>
  )
}
