import React, { useState, useRef, useEffect } from 'react'
import cat from './chibi-cat.png'
import { animated, useSpring } from "react-spring";
import { useLoader } from '@react-three/fiber'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'

function Scene() {
  const gltf = useLoader(GLTFLoader, '/Poimandres.gltf')
  return (
    <Suspense fallback={null}>
      <primitive object={gltf.scene} />
    </Suspense>
  )
}


export const Chibi = (props) => {
  const {stairsRef,tasksDone, daysCount,savedDay,id} = props

  const catRef = useRef(null);
  const [catRect, setCatRect] = useState({
    x: 0,
    y: 0,
    width: 0,
    height: 0
  })
  useEffect(() => {
    const div = stairsRef.current;
    const rect = div.getBoundingClientRect();
    setCatRect({
      x: rect.x,
      y: rect.y,
      z: rect.z,
      left: rect.left,
      width: rect.width,
      height: rect.height
    })
  }, [])
  const { width, height } = catRect
  let days=savedDay
  if(tasksDone){
    days=daysCount                                                                                                                                 
  }else{
    days=savedDay
  }


  const springProps = useSpring({
    from: { bottom: height * days + 'px', left: width * days + 'px', width: '100px', height: '100px', position: 'relative' },
    to: async next => {
      await next({ bottom: height * days +70 + 'px', left: width * days + 'px' })
      await next({ bottom: height * days + 30 + 'px', left: width * days + width / 2 + 'px' })
    },
    config: { duration: 500 }
  });

  return (
    <>
      <animated.img
        ref={catRef}
        src={cat}
        alt={'cat'}
        className='cat'
        style={
          springProps
        } />

    </>
  )
}
