import React, { useContext } from 'react'
import './navbar.scss'
import prize from '../../images/prize.png'
import { useState } from 'react';
import { TaskContext } from '../Context';

export const Navbar = ({count}) => {
  const [currentAccount, setCurrentAccount] = useState("");
  const {setModalText} = useContext(TaskContext)
  const {setModalShow} = useContext(TaskContext)
  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("Get MetaMask!");
        return;
      }

      const accounts = await ethereum.request({ method: "eth_requestAccounts" });

      console.log("Connected", accounts[0]);
      setCurrentAccount(accounts[0]);
      if(accounts[0]){
      setModalText("Connected")
      setModalShow(true)
      }
    } catch (error) {
      console.log(error)
    }
  }
  return (
    <div className="navbar">
      <a href="#contact">
      <div><span id="cash">Cash
      <img style={{width:'50px'}} src={prize}/>
      :{count}</span></div>
      </a>
      <a href="#news" onClick={connectWallet}>Wallet</a>
      <a href="#news" onClick={connectWallet}>Market place</a>
      <a href="#home">
      Next day <div><span id="time"></span></div>
      </a>
      <a href="#home">
      Account
      </a>
    </div>
  )
}



const Navigation = ({ web3Handler, account }) => {
  return (
      <Navbar expand="lg" bg="secondary" variant="dark">
          <Container>
              <Navbar.Brand href="http://www.dappuniversity.com/bootcamp">
                  <img src={market} width="40" height="40" className="" alt="" />
                  &nbsp; DApp NFT Marketplace
              </Navbar.Brand>
              <Navbar.Toggle aria-controls="responsive-navbar-nav" />
              <Navbar.Collapse id="responsive-navbar-nav">
                  <Nav className="me-auto">
                      <Nav.Link as={Link} to="/">Home</Nav.Link>
                      <Nav.Link as={Link} to="/create">Create</Nav.Link>
                      <Nav.Link as={Link} to="/my-listed-items">My Listed Items</Nav.Link>
                      <Nav.Link as={Link} to="/my-purchases">My Purchases</Nav.Link>
                  </Nav>
                  <Nav>
                      {account ? (
                          <Nav.Link
                              href={`https://etherscan.io/address/${account}`}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="button nav-button btn-sm mx-4">
                              <Button variant="outline-light">
                                  {account.slice(0, 5) + '...' + account.slice(38, 42)}
                              </Button>

                          </Nav.Link>
                      ) : (
                          <Button onClick={web3Handler} variant="outline-light">Connect Wallet</Button>
                      )}
                  </Nav>
              </Navbar.Collapse>
          </Container>
      </Navbar>
  )

}

export default Navigation;
