import React, { useRef, useState } from 'react'
import { Stair } from '../stair/Stair'
import { Chibi } from '../chibi/Chibi'
import { Tasks } from '../tasks/Tasks'
import { TaskContext } from '../Context'
import './main.css'
import { CountDownTimer } from './CountDownTimer'


export const MainPage = () => {
  const [tasksDone, setTasksDone] = useState(true)
  const stairsRef = useRef(null)
  const [isClicked, setIsClicked] = useState(true)
  let [daysCount, setDaysCount] = useState(1)
  let [position, setPosition] = useState(1)
  const [id, setId] = useState(60)
  window.onload = function () {

    var display = document.querySelector('#time'),
      timer = new CountDownTimer(3);

    timer.onTick(format).onTick(restart).start();
    function restart() {
      if (daysCount < 21) {
        if (this.expired()) {
          setTimeout(function () {
            timer.start()
          }, 1000);
          daysCount += 1;
          setDaysCount(daysCount)
          setTasksDone(false)
        }

      } else if (this.expired() && daysCount > 3) {
        setTimeout(function () {
          ;
        }, 1000);
        setDaysCount(0)
      }

    }
    function format(minutes, seconds) {
      minutes = minutes < 10 ? "0" + minutes : minutes;
      seconds = seconds < 10 ? "0" + seconds : seconds;
      display.textContent = minutes + ':' + seconds;
    }
  };

    if(tasksDone){
      position+=1
    }
    else{
      position-=1
    }
    console.log(tasksDone,position)


  return (
    <TaskContext.Provider value={{
      setTasksDone,
      setDaysCount,
      setIsClicked,
      setId
    }}>
      <div><span id="time"></span></div>

      <Stair
        stairsRef={stairsRef}
        isClicked={isClicked}
        id={id} />
      <Chibi
        daysCount={daysCount}
        stairsRef={stairsRef}
        tasksDone={tasksDone}
        position = {position} />
      <Tasks
        daysCount={daysCount}
        tasksDone={tasksDone}
        stairsRef={stairsRef}
        isClicked={isClicked}
        id={id}
      />
    </TaskContext.Provider>
  )
}
