import React, {Suspense, useState, useRef, useEffect } from 'react'
import cat from './chibi-cat.png'
import { animated, useSpring } from "react-spring";
import { useFrame, useLoader } from '@react-three/fiber'
import { GLTFLoader } from 'three/examples/jsm/loaders/GLTFLoader'
import { Canvas } from "@react-three/fiber";
import { OrbitControls } from "@react-three/drei";
import { Scene } from 'three';


const GltfModel = ({ modelPath, scale = 10, position = [0, 0, 0] }) => {
  const ref = useRef();
  const gltf = useLoader(GLTFLoader, modelPath);
  const [hovered, hover] = useState(false);

  // Subscribe this component to the render-loop, rotate the mesh every frame
  useFrame((state, delta) => (ref.current.rotation.y += 0.007));
  return (
    <>
      <primitive
        ref={ref}
        object={gltf.scene}
        position={position}
        scale={hovered ? scale * 2 : scale}
        onPointerOver={(event) => hover(true)}
        onPointerOut={(event) => hover(false)}
      />
    </>
  );
};

const ModelViewer = ({ modelPath, scale = 1, position = [0, 0, 0] }) => {
  return (
    <Canvas>
      <ambientLight />
      <spotLight />
      <pointLight />
      <Suspense fallback={null}>
        <GltfModel modelPath={modelPath} scale={scale} position={position} />
        <OrbitControls />
      </Suspense>
    </Canvas>
  );
};


export const Chibi = (props) => {
  const {stairsRef,tasksDone, daysCount,savedDay,id} = props

  const catRef = useRef(null);
  const [catRect, setCatRect] = useState({
    x: 0,
    y: 0,
    width: 0,
    height: 0
  })
  useEffect(() => {
    const div = stairsRef.current;
    const rect = div.getBoundingClientRect();
    setCatRect({
      x: rect.x,
      y: rect.y,
      z: rect.z,
      left: rect.left,
      width: rect.width,
      height: rect.height
    })
  }, [])
  const { width, height } = catRect
  let days=savedDay
  if(tasksDone){
    days=daysCount                                                                                                                                 
  }else{
    days=savedDay
  }


  const springProps = useSpring({
    from: { bottom: height * days + 'px', left: width * days + 'px', width: '100px', height: '100px', position: 'relative' },
    to: async next => {
      await next({ bottom: height * days +70 + 'px', left: width * days + 'px' })
      await next({ bottom: height * days + 30 + 'px', left: width * days + width / 2 + 'px' })
    },
    config: { duration: 500 }
  });

  return (
    <>
      <animated.img
        ref={catRef}
        src={cat}
        alt={'cat'}
        className='cat'
        style={
          springProps
        } />
      <ModelViewer scale="40" modelPath={"./scene.gltf"} />
    </>
  )
}
