import React, { useContext } from 'react'
import './navbar.scss'
import prize from '../../images/prize.png'
import { useState } from 'react';
import { TaskContext } from '../Context';
import {
  Link
} from "react-router-dom";
import { Navbar, Nav, Button, Container } from 'react-bootstrap'

export const Navigation = ({count,web3Handler, account}) => {
  const [currentAccount, setCurrentAccount] = useState("");
  const {setModalText} = useContext(TaskContext)
  const {setModalShow} = useContext(TaskContext)
  const connectWallet = async () => {
    try {
      const { ethereum } = window;

      if (!ethereum) {
        alert("Get MetaMask!");
        return;
      }

      const accounts = await ethereum.request({ method: "eth_requestAccounts" });

      console.log("Connected", accounts[0]);
      setCurrentAccount(accounts[0]);
      if(accounts[0]){
      setModalText("Connected")
      setModalShow(true)
      }
    } catch (error) {
      console.log(error)
    }
  }
  return (
    <>
    <div className="navbar">
      <a href="#contact">
      <div><span id="cash">Cash
      <img style={{width:'50px'}} src={prize}/>
      :{count}</span></div>
      </a>
      <a href="#news" onClick={connectWallet}>Wallet</a>
      <a href="#news" onClick={connectWallet}>Market place</a>
      <a href="#home">
      Next day <div><span id="time"></span></div>
      </a>
      <a href="#home">
      Account
      </a>
    </div>
    <Navbar expand="lg" bg="secondary" variant="dark">
          <Container>
              <Navbar.Brand href="http://www.dappuniversity.com/bootcamp">
                  <img src={''} width="40" height="40" className="" alt="" />
                  &nbsp; DApp NFT Marketplace
              </Navbar.Brand>
              <Navbar.Toggle aria-controls="responsive-navbar-nav" />
              <Navbar.Collapse id="responsive-navbar-nav">

              </Navbar.Collapse>
          </Container>
      </Navbar>
      </>
  )
}
